int UnionUpdateIntTypeSpecFldOfAutoFldDefRec(char *desResName,char *desFldName,char *fldName,int fldValue)
{
	int	ret;
	char	recStr[8192*2+1];
	char	primaryKey[1024+1];
	int	lenOfRecStr = 0;

	// 拼关键字
	memset(recStr,0,sizeof(recStr));
	ret = UnionPutRecFldIntoRecStr("desResName",desResName,strlen(desResName),recStr+lenOfRecStr,sizeof(recStr)-lenOfRecStr);
	if (ret < 0)
	{
		UnionUserErrLog("in UnionUpdateIntTypeSpecFldOfAutoFldDefRec:: UnionPutRecFldIntoRecStr [desResName] failure!\n");
		return(ret);
	}
	lenOfRecStr += ret;
	ret = UnionPutRecFldIntoRecStr("desFldName",desFldName,strlen(desFldName),recStr+lenOfRecStr,sizeof(recStr)-lenOfRecStr);
	if (ret < 0)
	{
		UnionUserErrLog("in UnionUpdateIntTypeSpecFldOfAutoFldDefRec:: UnionPutRecFldIntoRecStr [desFldName] failure!\n");
		return(ret);
	}
	lenOfRecStr += ret;
	strcpy(primaryKey,recStr);

	// 组记录
	memset(recStr,0,sizeof(recStr));
	lenOfRecStr = 0;
	if ((ret = UnionPutIntTypeRecFldIntoRecStr(fldName,fldValue,recStr+lenOfRecStr,sizeof(recStr)-lenOfRecStr)) < 0)
	{
		UnionUserErrLog("in UnionUpdateIntTypeSpecFldOfAutoFldDefRec:: UnionPutIntTypeRecFldIntoRecStr [%s] failure!\n",fldName);
		return(ret);
	}
	lenOfRecStr += ret;

	//赋加自动修改域
	if ((lenOfRecStr = UnionAutoAppendDBRecUpdateAttr(recStr,lenOfRecStr,sizeof(recStr))) < 0)
	{
		UnionUserErrLog("in UnionUpdateIntTypeSpecFldOfAutoFldDefRec:: UnionAutoAppendDBRecUpdateAttr!\n");
		return(lenOfRecStr);
	}

	// 修改记录
	if ((ret = UnionUpdateUniqueObjectRecord(conTBLNameAutoFldDef,primaryKey,recStr,lenOfRecStr)) < 0)
	{
		UnionUserErrLog("in UnionUpdateIntTypeSpecFldOfAutoFldDefRec:: UnionUpdateUniqueObjectRecord!\n");
		return(ret);
	}

	return(ret);
}



